mapper.select:
select * from opening_inventory where 1=1 

	and companyId = #{companyId}


	and cuserId = #{cuserId}


	limit #{start},#{size}

select count(*) from opening_inventory where 1=1 

	and companyId = #{companyId}


	and cuserId = #{cuserId}

mapper.update:
UPDATE 
	opening_inventory  
SET 
	status = #{condition.status} 

	,isClaimed = #{condition.isClaimed} 


		,etime = #{condition.etime} 


WHERE id = #{condition.id}
