mapper.select:
select * from product where deleted!=1

	and companyId = #{companyId}


	and typeId = #{typeId} 


	and (name like concat('%', #{keywords}, '%') or code like concat('%', #{keywords}, '%'))   

order by ctime desc

	limit #{start},#{size}

select count(*) from product where  deleted!=1

	and companyId = #{companyId}


	and typeId = #{typeId} 


	and (name like concat('%', #{keywords}, '%') or code like concat('%', #{keywords}, '%'))

select * from product where deleted != 1 

	and companyId = #{companyId} 


	and typeId = #{typeId}


	limit #{start},#{size}

select count(*) from product where deleted != 1 

	and companyId = #{companyId} 


	and typeId = #{typeId}
