mapper.select:
SELECT pdp.*, sup.supplierName, pro.name productName, pro.spec, pt.name productType FROM (SELECT pd.id, pd.productId, pd.number, pd.price, pd.totalMoney, pd.notInNumber, p.code, p.supplierId, p.ctime, p.cuserId, p.status, p.companyId, p.initDate FROM purchase_detail pd LEFT JOIN purchase p ON pd.purchaseId = p.id ) pdp LEFT JOIN supplier sup ON pdp.supplierId = sup.id LEFT JOIN product pro ON pdp.productId = pro.id LEFT JOIN product_type pt ON pro.typeId = pt.id WHERE deleted != 1
and pdp.companyId = #{companyId}
ORDER BY ctime desc
limit #{start},#{size}

SELECT pdp.*, sup.supplierName, pro.name productName, pro.spec, pt.name productType FROM (SELECT pd.id, pd.productId, pd.number, pd.price, pd.totalMoney, pd.notInNumber, p.code, p.supplierId, p.ctime, p.cuserId, p.status, p.companyId, p.initDate FROM purchase_detail pd LEFT JOIN purchase p ON pd.purchaseId = p.id ) pdp LEFT JOIN supplier sup ON pdp.supplierId = sup.id LEFT JOIN product pro ON pdp.productId = pro.id LEFT JOIN product_type pt ON pro.typeId = pt.id WHERE deleted != 1
and pdp.companyId = #{companyId}
and pro.id = #{searchData.productId}
and pdp.code like concat('%', #{searchData.code}, '%')
and sup.supplierName like concat('%', #{searchData.supplierName},'%')
and pdp.initDate > #{searchData.startDate} and pdp.initDate < #{searchData.endDate}
ORDER BY pdp.ctime desc
limit #{start},#{size}

SELECT count(*) FROM (SELECT pd.id, pd.productId, pd.number, pd.price, pd.totalMoney, pd.notInNumber, p.code, p.supplierId, p.ctime, p.cuserId, p.status, p.companyId, p.initDate FROM purchase_detail pd LEFT JOIN purchase p ON pd.purchaseId = p.id ) pdp LEFT JOIN supplier sup ON pdp.supplierId = sup.id LEFT JOIN product pro ON pdp.productId = pro.id LEFT JOIN product_type pt ON pro.typeId = pt.id WHERE deleted != 1
and pdp.companyId = #{companyId}
and pro.id = #{searchData.productId}
and pdp.code like concat('%', #{searchData.code}, '%')
and sup.supplierName like concat('%', #{searchData.supplierName},'%')
and pdp.initDate > #{searchData.startDate} and pdp.initDate < #{searchData.endDate}
