mapper.select:
select ja.* from journal_account ja left join stock s on ja.stockId = s.id left join warehouse w on s.warehouseId = w.id left join product p on s.productId = p.id where ja.companyId= #{companyId}
AND (ja.orderCode like concat('%',#{keywords},'%') or p.code like concat('%',#{keywords},'%') or p.name like concat('%',#{keywords},'%'))
AND ja.orderCode like concat('%',#{code},'%')
AND p.code like concat('%',#{productCode},'%')
AND p.name like concat('%',#{productName},'%')
AND w.id =#{warehouseId}
order by ${sort} ${order}
limit #{start},#{size}

select count(*) from journal_account ja left join stock s on ja.stockId = s.id left join warehouse w on s.warehouseId = w.id left join product p on s.productId = p.id where ja.companyId=#{companyId}
AND (ja.orderCode like concat('%',#{keywords},'%') or p.code like concat('%',#{keywords},'%') or p.name like concat('%',#{keywords},'%'))
AND ja.orderCode like concat('%',#{code},'%')
AND p.code like concat('%',#{productCode},'%')
AND p.name like concat('%',#{productName},'%')
AND w.id =#{warehouseId}
